CREATE TABLE dbo.[IndividualResults] (AthleteId uniqueidentifier NOT NULL, Category nvarchar(30), Month tinyint NOT NULL, Year int NOT NULL, Distance int DEFAULT 0, MovingTime int DEFAULT 0, Points float DEFAULT 0, CONSTRAINT [Pk] PRIMARY KEY CLUSTERED (AthleteId, Category, Month, Year), FOREIGN KEY (AthleteId) REFERENCES dbo.Athletes(Id))